import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const orderDetails = createAsyncThunk(
  "detailOrder/orderDetails",
  async (orderId, { getState }) => {
    const state = getState();
    const userInfo = state.signin.userInfo;

    try {
      const response = await axios.get(
        `http://localhost:5000/orders/${orderId}`,
        {
          headers: {
            authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message;
      return message;
    }
  }
);

export const orderPayment = createAsyncThunk(
  "paymentSlice/orderPayment",
  async (data, { getState }) => {
    console.log(data);
    const state = getState();
    const userInfo = state.signin.userInfo;
    const orderId = data.orderId;
    const details = data.details;
    console.log(orderId, details);
    try {
      const response = await axios.put(
        `http://localhost:5000/orders/${orderId}/pay`,
        details,
        {
          headers: {
            authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message;
      return message;
    }
  }
);

const orderSlice = createSlice({
  name: "detailOrder",
  initialState: {
    detailLoading: true,
    detailError: "",
    detailedOrder: "",
    paymentLoading: false,
    paymentSuccess: false,
    paymentFailed: "",
  },
  reducers: {},
  extraReducers(builder) {
    builder
      .addCase(orderDetails.pending, (state) => {
        state.detailLoading = true;
      })
      .addCase(orderDetails.fulfilled, (state, action) => {
        state.detailLoading = false;
        state.detailedOrder = action.payload;
      })
      .addCase(orderDetails.rejected, (state, action) => {
        state.detailLoading = false;
        state.detailError = action.payload;
      })

      .addCase(orderPayment.pending, (state) => {
        state.paymentLoading = true;
      })
      .addCase(orderPayment.fulfilled, (state, action) => {
        state.paymentLoading = false;
        state.paymentSuccess = "Order is paid";
        state.detailedOrder = action.payload;
        console.log(action.payload);
      })
      .addCase(orderPayment.rejected, (state, action) => {
        state.paymentLoading = false;
        state.paymentFailed = action.payload;
      });
  },
});

export default orderSlice.reducer;
