import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const initialState = {
  clickedProd: [],
  loadingClicked: false,
  clickedError: false,
  isWorking: false,
};

export const getClickedProduct = createAsyncThunk(
  "clickedProductDetail/getClickedProduct",
  (data) => {
    return fetch("http://localhost:5000/products/:id", {
      method: "post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ data }),
    }).then((response) => response.json());
  }
);

const clickedProductSlice = createSlice({
  name: "clickedProductDetail",
  initialState,
  reducer: {
    isWorking: (state) => (state.isWorking = true),
  },
  extraReducer: {
    [getClickedProduct.pending]: (state) => {
      state.loadingClicked = true;
    },
    [getClickedProduct.fulfilled]: (state, action) => {
      state.loadingClicked = false;
      state.clickedProd = action.payload;
      console.log(action);
    },
    [getClickedProduct.rejected]: (state) => {
      state.loadingClicked = false;
      state.clickedError = true;
    },
  },
});

export default clickedProductSlice.reducer;
export const { isworking } = clickedProductSlice.actions;
