import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams, useSearchParams } from "react-router-dom";
import { addItemToCart } from "../features/addToCartSlice";

const CartScreen = () => {
  const params = useParams();
  const [searchParams] = useSearchParams();
  const quantity = searchParams.get("qty");
  console.log(quantity);
  const qty = quantity ? quantity : 1;
  const { cartItems } = useSelector((state) => state.addToCart);
  const dispatch = useDispatch();

  useEffect(() => {
    if (params.id) {
      dispatch(addItemToCart(params.id, qty));
    }
  }, [dispatch, params.id, qty]);

  return (
    <div>
      <h1>Cart Screen</h1>
      <p>{`ADD TO CART: ProductID: ${params.id} Qty: ${qty}`}</p>
    </div>
  );
};

export default CartScreen;
