import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const getProductDetails = createAsyncThunk(
  "product/getProductDetails",
  (id) => {
    console.log(id);
    return fetch(`"http://localhost:5000/products//${id}"`)
      .then((response) => response.json())
      .then(console.log);
  }
);

const prodDetailSlice = createSlice({
  name: prodDetail,
  initialState: { productDetails: {} },
  extraReducer: {
    [getProductDetails.pending]: (state) => {
      //   state.isLoading = true;
    },
    [getProductDetails.fulfilled]: (state, action) => {
      //   state.isLoading = false;
      state.productDetail = action.payload;
    },
    [getProductDetails.rejected]: (state) => {
      //   state.isLoading = false;
      //   state.error = true;
    },
  },
});

export default prodDetailSlice.reducer;
