import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const initialState = {
  productList: [],
  isLoading: false,
};

export const getProductList = createAsyncThunk("product/getProductList", () => {
  fetch("http://localhost:5000/products").then((response) => response.json());
}).catch((err) => {
  return err;
});

const productSlice = createSlice({
  name: "products",
  initialState,
  reducers: {
    productList: (state) => {},
  },
});

export default productSlice.reducer;
export const { productList } = productSlice.actions;
