import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";

const OrderScreen = () => {
  const dispatch = useDispatch();

  const params = useParams();
  const orderId = params.id;
  const { thisOrder, orderLoading, orderError, success } = useSelector(
    (state) => state.order
  );
  const { currentOrder } = useSelector((state) => state.orderList);

  const sOrder = thisOrder ? thisOrder : currentOrder;
  console.log(sOrder);
  useEffect(() => {}, [thisOrder]);

  return orderLoading ? (
    <LoadingBox></LoadingBox>
  ) : orderError ? (
    <MessageBox variant="danger">{orderError}</MessageBox>
  ) : (
    <div>
      <h1>Order {sOrder._id}</h1>
      <div className="row top">
        <div className="col-2">
          <ul>
            <li>
              <div className="card card-body">
                <h2>Shipping</h2>
                <p>
                  <strong>Name: </strong> {sOrder.shippingAddress.fullName}{" "}
                  <br />
                  <strong>Address: </strong>
                  {sOrder.shippingAddress.address},{" "}
                  {sOrder.shippingAddress.city}
                  {sOrder.shippingAddress.state},{" "}
                  {sOrder.shippingAddress.postalCode},{" "}
                  {sOrder.shippingAddress.country}
                </p>
                {sOrder.isDelivered ? (
                  <MessageBox variant="success">
                    {sOrder.deliveredAt}
                  </MessageBox>
                ) : (
                  <MessageBox variant="danger">Not Deliverd</MessageBox>
                )}
              </div>
            </li>

            <li>
              <div className="card card-body">
                <h2>Payment</h2>
                <p>
                  <strong>Method:</strong> {sOrder.paymentMethod}
                </p>
                {sOrder.isPaid ? (
                  <MessageBox variant="success">
                    Paid at {sOrder.paidAt}
                  </MessageBox>
                ) : (
                  <MessageBox variant="danger">Not Paid</MessageBox>
                )}
              </div>
            </li>

            <li>
              <div className="card card-body">
                <h2>Order Items</h2>
                <ul>
                  {sOrder.orderItems.map((item) => {
                    return (
                      <li key={item.product}>
                        <div className="row">
                          <div>
                            <img
                              src={item.image}
                              alt={item.name}
                              className="small"
                            ></img>
                          </div>
                          <div className="min-30rem">
                            <Link to={`/product/${item.product}`}>
                              {item.name}
                            </Link>
                          </div>

                          <div>
                            {item.qty} x ${item.price} = $
                            {item.price * item.qty}
                          </div>
                        </div>
                      </li>
                    );
                  })}
                </ul>
              </div>
            </li>
          </ul>
        </div>

        <div className="col-1">
          <div className="card card-body">
            <ul>
              <li>
                <h2>Order Summary</h2>
              </li>
              <li>
                <div className="row">
                  <div>Items</div>
                  <div>${sOrder.itemsPrice.toFixed(2)}</div>
                </div>
              </li>

              <li>
                <div className="row">
                  <div>Shipping</div>
                  <div>${sOrder.shippingPrice.toFixed(2)}</div>
                </div>
              </li>

              <li>
                <div className="row">
                  <div>Tax</div>
                  <div>${sOrder.taxPrice.toFixed(2)}</div>
                </div>
              </li>

              <li>
                <div className="row">
                  <div>
                    <strong>Order Total</strong>
                  </div>
                  <div>
                    <strong>${sOrderr.totalPrice.toFixed(2)}</strong>
                  </div>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderScreen;
