import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const orderPayment = createAsyncThunk(
  "paymentSlice/orderPayment",
  async (data, { getState }) => {
    const state = getState();
    const userInfo = state.signin.userInfo;
    const orderId = data.orderId;
    const details = data.details;

    try {
      const response = await axios.put(
        `http://localhost:5000/orders/${orderId}/pay`,
        details,
        {
          headers: {
            authorization: `Bearer ${userInfo.token}`,
          },
        }
      );
      console.log(response.data);
      return response.data;
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message;
      return message;
    }
  }
);

const paymentSlice = createSlice({
  name: "payment",
  initialState: {
    paymentMethod: "",
    paymentLoading: false,
    paymentSuccess: false,
    paymentFailed: "",
    paymentData: "",
  },
  reducers: {
    savePaymentMethod: (state, { payload }) => {
      state.paymentMethod = payload;
    },
    paymentReset: (state) => {
      return (state = {});
    },
  },
  extraReducers(builder) {
    builder
      .addCase(orderPayment.pending, (state) => {
        state.ppaymentLoading = true;
      })
      .addCase(orderPayment.fulfilled, (state, action) => {
        state.paymentLoading = false;
        state.paymentSuccess = "Order is paid";
        state.paymentData = action.payload;
      })
      .addCase(orderPayment.rejected, (state, action) => {
        state.paymentLoading = false;
        state.paymentFailed = action.payload;
        console.log(action.payload);
      });
  },
});

export default paymentSlice.reducer;
export const { savePaymentMethod, paymentReset } = paymentSlice.actions;
