const orderListSlice = createSlice({
  name: "orderList",
  initialState: {
    loading: false,
    error: "",
    orderlist: [],
  },
  extraReducers(builder) {
    builder.addCase(myOrders.pending, (state) => {
      state.loading = true;
    });
  },
});

export default orderListSlice.reducer;
