import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { detailedUser } from "../features/profileSlice";

const ProfileScreen = () => {
  const { userInfo } = useSelector((state) => state.signin);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailedUser(userInfo.id));
  }, []);

  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <h1>Create Your Account</h1>
        </div>
        {newuserLoading && <LoadingBox />}
        {registerError && (
          <MessageBox variant="danger">{errorMessage}</MessageBox>
        )}
        <div>
          <label htmlFor="name">Name</label>
          <input
            type="name"
            id="name"
            value={name}
            placeholder="Enter Name"
            required
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="email">Email Address</label>
          <input
            type="email"
            id="email"
            value={email}
            placeholder="Enter Email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            value={password}
            id="password"
            placeholder="Enter Password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="confrimpassword">Confirm Password</label>
          <input
            type="confirmpassword"
            value={confirmPassword}
            id="confirmpassword"
            placeholder="Re-enter Password"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>

        <div>
          <label />
          <button className="primary" type="submit">
            Register
          </button>
        </div>
        <div>
          <label />
          <div>
            {`Alredy have an account? `}
            <Link to="/signin">Sign In</Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ProfileScreen;
