import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { orderDetails } from "../features/orderSlice";
import { PayPalButtons, usePayPalScriptReducer } from "@paypal/react-paypal-js";
import axios from "axios";
import { paymentReset } from "../features/paymentSlice";
import { orderPayment } from "../features/orderSlice";

const OrderScreen = () => {
  const dispatch = useDispatch();
  const [{ isPending }, paypalDispatch] = usePayPalScriptReducer();
  const params = useParams();
  const orderId = params.id;
  const {
    detailedOrder,
    detailLoading,
    detailError,
    paymentSuccess,
    paymentLoading,
    paymentFail,
  } = useSelector((state) => state.detailOrder);
  // const { paymentSuccess, paymentFail, paymentData, paymentLoading } =
  //   useSelector((state) => state.payment);

  const createOrder = (data, actions) => {
    return actions.order
      .create({
        purchase_units: [
          {
            amount: { value: detailedOrder.totalPrice },
          },
        ],
      })
      .then((id) => id);
  };

  const onApprove = (data, actions) => {
    return actions.order.capture().then((details) => {
      dispatch(orderPayment({ details, orderId }));
    });
  };

  const onError = () => {};
  console.log(paymentData);
  useEffect(() => {
    if (
      !detailedOrder ||
      paymentSuccess ||
      (detailedOrder._id && detailedOrder._id !== orderId)
    ) {
      dispatch(orderDetails(orderId));
      if (paymentSuccess) {
        dispatch(paymentReset());
      }
    } else {
      const loadPaypalScript = async () => {
        const { data } = await axios.get("http://localhost:5000/keys/paypal");
        paypalDispatch({
          type: "resetOptions",
          value: {
            "client-id": data,
            currency: "USD",
          },
        });
        paypalDispatch({ type: "setLoadingStatus", value: "pending" });
      };

      loadPaypalScript();
    }
  }, [dispatch, orderId, paypalDispatch, detailedOrder, paymentSuccess]);

  return detailLoading ? (
    <LoadingBox></LoadingBox>
  ) : detailError ? (
    <MessageBox variant="danger"></MessageBox>
  ) : (
    <div>
      <h1>Order {detailedOrder._id}</h1>
      <div className="row top">
        <div className="col-2">
          <ul>
            <li>
              <div className="card card-body">
                <h2>Shipping</h2>
                <p>
                  <strong>Name: </strong>{" "}
                  {detailedOrder.shippingAddress.fullName} <br />
                  <strong>Address: </strong>
                  {detailedOrder.shippingAddress.address},{" "}
                  {detailedOrder.shippingAddress.city},{" "}
                  {detailedOrder.shippingAddress.state},{" "}
                  {detailedOrder.shippingAddress.postalCode},{" "}
                  {detailedOrder.shippingAddress.country}
                </p>
                {detailedOrder.isDelivered ? (
                  <MessageBox variant="success">
                    {detailedOrder.deliveredAt}
                  </MessageBox>
                ) : (
                  <MessageBox variant="danger">Not Deliverd</MessageBox>
                )}
              </div>
            </li>

            <li>
              <div className="card card-body">
                <h2>Payment</h2>
                <p>
                  <strong>Method:</strong> {detailedOrder.paymentMethod}
                </p>
                {detailedOrder.isPaid ? (
                  <MessageBox variant="success">
                    Paid at {detailedOrder.paidAt}
                  </MessageBox>
                ) : (
                  <MessageBox variant="danger">Not Paid</MessageBox>
                )}
              </div>
            </li>

            <li>
              <div className="card card-body">
                <h2>Order Items</h2>
                <ul>
                  {detailedOrder.orderItems.map((item) => {
                    return (
                      <li key={item.product}>
                        <div className="row">
                          <div>
                            <img
                              src={item.image}
                              alt={item.name}
                              className="small"
                            ></img>
                          </div>
                          <div className="min-30rem">
                            <Link to={`/product/${item.product}`}>
                              {item.name}
                            </Link>
                          </div>

                          <div>
                            {item.qty} x ${item.price} = $
                            {item.price * item.qty}
                          </div>
                        </div>
                      </li>
                    );
                  })}
                </ul>
              </div>
            </li>
          </ul>
        </div>

        <div className="col-1">
          <div className="card card-body">
            <ul>
              <li>
                <h2>Order Summary</h2>
              </li>
              <li>
                <div className="row">
                  <div>Items</div>
                  <div>${detailedOrder.itemsPrice.toFixed(2)}</div>
                </div>
              </li>

              <li>
                <div className="row">
                  <div>Shipping</div>
                  <div>${detailedOrder.shippingPrice.toFixed(2)}</div>
                </div>
              </li>

              <li>
                <div className="row">
                  <div>Tax</div>
                  <div>${detailedOrder.taxPrice.toFixed(2)}</div>
                </div>
              </li>

              <li>
                <div className="row">
                  <div>
                    <strong>Order Total</strong>
                  </div>
                  <div>
                    <strong>${detailedOrder.totalPrice.toFixed(2)}</strong>
                  </div>
                </div>
              </li>
              <li>
                {!detailedOrder.isPaid && (
                  <div>
                    {isPending ? (
                      <LoadingBox />
                    ) : (
                      <div>
                        <PayPalButtons
                          createOrder={createOrder}
                          onApprove={onApprove}
                          onError={onError}
                        ></PayPalButtons>
                      </div>
                    )}
                    {paymentLoading && <LoadingBox></LoadingBox>}
                  </div>
                )}
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderScreen;
