import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { detailedUser } from "../features/profileSlice";

const ProfileScreen = () => {
  const { userInfo } = useSelector((state) => state.signin);
  const { detailedInfo, loading, error } = useSelector(
    (state) => state.profile
  );
  const dispatch = useDispatch();
  console.log(detailedInfo);
  useEffect(() => {
    dispatch(detailedUser(userInfo.id));
  }, [dispatch, userInfo.id]);

  const submitHandler = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <h1>User Profile</h1>
        </div>
        {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger"></MessageBox>
        ) : (
          <>
            <div>
              <label htmlFor="name">Name</label>
              <input
                type="text"
                id="name"
                value={detailedInfo.name}
                placeholder="Enter Name"
              />
            </div>

            <div>
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                value={detailedInfo.email}
                placeholder="Enter Email"
              />
            </div>

            <div>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                placeholder="Enter Password"
              />
            </div>

            <div>
              <label htmlFor="confrimpassword">Confirm Password</label>
              <input
                type="confirmpassword"
                id="confirmpassword"
                placeholder="Re-enter Password"
              />
            </div>

            <div>
              <label />
              <button className="primary" type="submit">
                Update
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
};

export default ProfileScreen;
