import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const initialState = {
  productList: [],
  isLoading: false,
  error: false,
  clickedProduct: {},
  clickedLoading: false,
  clickedError: false,
};

export const getProductList = createAsyncThunk("product/getProductList", () => {
  return fetch("http://localhost:5000/products").then((response) =>
    response.json()
  );
});

export const getClickedProduct = createAsyncThunk(
  "product/getClickedProduct",
  (id) => {
    console.log(id);
    return fetch("http://localhost:5000/products/:id")
      .then((response) => response.json())
      .then(console.log);
  }
);

const productSlice = createSlice({
  name: "products",
  initialState,
  //   reducers: {
  //     productLists: (state) => {},
  //   },
  extraReducers: {
    [getProductList.pending]: (state) => {
      state.isLoading = true;
    },
    [getProductList.fulfilled]: (state, action) => {
      state.isLoading = false;
      state.productList = action.payload;
    },
    [getProductList.rejected]: (state) => {
      state.isLoading = false;
      state.error = true;
    },

    [getClickedProduct.pending]: (state) => {
      state.clickedLoading = true;
    },
    [getClickedProduct.fulfilled]: (state, action) => {
      state.clickedLoading = false;
      state.productDetail = action.payload;
    },
    [getClickedProduct.rejected]: (state) => {
      state.clickedLoading = false;
      state.clickedError = true;
    },
  },
});

export default productSlice.reducer;
// export const { productLists } = productSlice.actions;
