import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { myOrders } from "../features/orderListSlice";

const OrderHistoryScreen = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { orders, loading, error } = useSelector((state) => state.orderList);

  useEffect(() => {
    dispatch(myOrders());
  }, [dispatch, myOrders]);

  return (
    <div>
      <h1>Order History</h1>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger"></MessageBox>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>DATE</th>
              <th>TOTAL</th>
              <th>PAID</th>
              <th>DELIVERED</th>
              <th>ACTIONS</th>
            </tr>
          </thead>
          <tbody>
            {/* {orders.map((order) => {
              {
                console.log(order);
              } */}
            <tr key={orders[0]._id}>
              <td>{orders[0]._id}</td>
              <td>{orders[0].createdAt}</td>
              <td>{orders[0].totalPrice}</td>
              <td>{orders[0].isPaid ? order.paidAt : "No"}</td>
              <td>{orders[0].isDelivered ? order.deliveredAt : "No"}</td>
              <td>
                <button
                  type="button"
                  className="small"
                  onClick={() => navigate(`/order/${order._id}`)}
                >
                  Details
                </button>
              </td>
            </tr>
            ; })}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default OrderHistoryScreen;
