import { data } from "./data";
import { createStore, compose, applyMiddleware } from "redux";
import thunk from "redux-thunk";

// to create a store, we need to define 2 things. the initialstate and the reducer.
// the reducer, is a function that accept 2 parameters. the state and the action. and returns
// a new state.
// the configurestore accepts 2 params our reducer and our initial state.

const initialState = {};

const reducer = (state, action) => {
  return { products: data.products };
};

const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
// this line is to show store in the browser.

const store = createStore(
  reducer,
  initialState,
  composeEnhancer(applyMiddleware(thunk))
);

export default store;
