import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { userRegister } from "../features/registerSlice";

const RegisterScreen = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  // const [loggedIn, set]
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { errorMessage, isRegistered, registerError, userLoading } =
    useSelector((state) => state.register);

  useEffect(() => {
    if (isRegistered) {
      navigate(-1);
      setName("");
      setEmail("");
      setPassword("");
    }
  }, [isRegistered, navigate, dispatch]);

  const submitHandler = (event) => {
    event.preventDefault();
    dispatch(userRegister({ name, email, password }));
  };

  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <h1>Register</h1>
        </div>
        {userLoading && <LoadingBox />}
        {registerError && (
          <MessageBox variant="danger">{errorMessage}</MessageBox>
        )}
        <div>
          <label htmlFor="name">Name</label>
          <input
            type="name"
            id="name"
            value={name}
            placeholder="Enter Name"
            required
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="email">Email Address</label>
          <input
            type="email"
            id="email"
            value={email}
            placeholder="Enter Email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="password">password</label>
          <input
            type="password"
            value={password}
            id="password"
            placeholder="Enter Password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <label />
          <button className="primary" type="submit">
            Register
          </button>
        </div>
        <div>
          <label />
          <div>
            {`Alredy Have An Account? `}
            <Link to="/signin">Sign In</Link>
          </div>
        </div>
      </form>
    </div>
  );
};

export default RegisterScreen;
