import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { detailedUser } from "../features/profileSlice";

const ProfileScreen = () => {
  const { userInfo } = useSelector((state) => state.signin);
  const { detailedUser } = useSelector((state) => state.profile);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(detailedUser(userInfo.id));
  }, [dispatch, userInfo.id]);

  return (
    <div>
      <form className="form">
        <div>
          <h1>Create Your Account</h1>
        </div>

        <div>
          <label htmlFor="name">Name</label>
          <input
            type="name"
            id="name"
            value={"detailedUser.name"}
            placeholder="Enter Name"
          />
        </div>

        <div>
          <label htmlFor="email">Email Address</label>
          <input type="email" id="email" placeholder="Enter Email" />
        </div>

        <div>
          <label htmlFor="password">Password</label>
          <input type="password" id="password" placeholder="Enter Password" />
        </div>

        <div>
          <label htmlFor="confrimpassword">Confirm Password</label>
          <input
            type="confirmpassword"
            id="confirmpassword"
            placeholder="Re-enter Password"
          />
        </div>

        <div>
          <label />
          <button className="primary" type="submit">
            Edit
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProfileScreen;
