import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

import axios from "axios";

export const addItemToCart = createAsyncThunk(
  "addToCart/addItemToCart",
  async ({ id, qty }) => {
    try {
      const response = await axios.post("http://localhost:5000/products/:id", {
        id,
      });

      return {
        name: response.data[0].name,
        image: response.data[0].image,
        price: response.data[0].price,
        countInStock: response.data[0].countInStock,
        product: response.data[0].id,
        qty,
      };
    } catch {}
  }
);
const addToCartSlice = createSlice({
  name: "addToCart",
  initialState: {
    cartItems: [],
  },
  extraReducers(builder) {
    builder.addCase(addItemToCart.fulfilled, (state, action) => {
      const item = action.payload;
      if (state.cartItems.length > 0) {
        const newCartItems = state.cartItems.filter((inCartItem) => {
          return inCartItem.product !== item.product;
        });

        if (newCartItems.length > 0) {
          state.cartItems = [...newCartItems, item];
        } else {
          state.cartItems = [item];
        }
      } else {
        state.cartItems = [item];
      }
    });
  },
});

export default addToCartSlice.reducer;
