import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

export const detailedUser = createAsyncThunk(
  "profile/detailedUser",
  async (userId, { rejectWithValue, getState }) => {
    const state = getState();
    const userInfo = state.signin.userInfo;

    try {
      const response = await axios.get(
        `http://localhost:5000/users/${userId}`,
        { headers: { authorization: `Bearer ${userInfo.token}` } }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data.message);
    }
  }
);

const profileSlice = createSlice({
  name: "profile",
  initialState: {
    loading: false,
    detailedInfo: "",
    error: false,
  },
  extraReducers(builder) {
    builder
      .addCase(detailedUser.pending, (state) => {
        state.loading = true;
      })
      .addCase(detailedUser.fulfilled, (state, action) => {
        state.loading = false;
        state.detailedInfo = action.payload;
      })
      .addCase(detailedUser.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
        console.log(action.payload);
      });
  },
});

export default profileSlice.reducer;
